import React, { useState } from 'react';


const App = () => {
    const [inputList, setInputList] = useState("Your to-dos will appear here...");
    const [listArr, setListArr] = useState([]);



    const itemEvent = (e) => {
        console.log("Change");
        setInputList(e.target.value);

    }
    const itemList = (e) => {
        setListArr((oldItems)=>{
return [...oldItems,inputList];
        })
    }
    return <>
        <div className="outer_div">
            <div className="inner_div">
                <h1>To Do List</h1>
                <input type="text" placeholder="Add an item" onChange={itemEvent} />
                <button onClick={itemList}>+</button>

                <ol>
                    {
                        listArr.map((item) => {
                            return <li>{item}</li>
                        })
                    }
                </ol>
            </div>

        </div>
    </>

}
export default App;
